frappe.ui.form.on('Item child table', {
    item_code: function (frm, cdt, cdn) {
        let row = locals[cdt][cdn];

        if (row.item_code) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function (response) {
                    if (response.message) {
                        let item = response.message;
                        let uoms = item.uoms || [];
                        let uom_names = uoms.map(u => u.uom);

                        // Reset UOM first
                        frappe.model.set_value(cdt, cdn, "uom", "");

                        // Update UOM field options dynamically
                        frm.fields_dict.item_table.grid.update_docfield_property(
                            'uom',
                            'options',
                            uom_names.join("\n")
                        );

                        if (uom_names.length > 0) {
                            frappe.model.set_value(cdt, cdn, "uom", uom_names[0]);
                        } else {
                            console.warn("No UOMs found to set default.");
                        }
                    } else {
                        console.warn("Item not found for item_code:", row.item_code);
                    }
                }
            });
        } else {
            console.log("No item_code selected.");
        }
    }
});

frappe.ui.form.on('Item child table', {
    item_code: function(frm, cdt, cdn) {
        frappe.msgprint("üÜï Item Code field triggered");

        let row = locals[cdt][cdn];
        frappe.msgprint("üìå Selected Item Code: " + row.item_code);

        if (row.item_code) {
            if (frm.doc.custom_price_list) {
                frappe.msgprint("‚úÖ Price List exists: " + frm.doc.custom_price_list);

                frappe.call({
                    method: "frappe.client.get_list",
                    args: {
                        doctype: "Item Price",
                        filters: {
                            item_code: row.item_code,
                            price_list: frm.doc.custom_price_list
                        },
                        fields: ["price_list_rate"],
                        limit_page_length: 1
                    },
                    callback: function(res) {
                        frappe.msgprint("üì° API Call Returned for: " + row.item_code);

                        if (res.message && res.message.length > 0) {
                            let price = res.message[0].price_list_rate;
                            frappe.msgprint("‚úÖ Found Price: " + price);
                            frappe.model.set_value(cdt, cdn, "sale_price", price);

                            // also trigger discount calculation when sale price is fetched
                            calculate_discount(row, cdt, cdn);
                        } else {
                            frappe.msgprint("‚ùå No Price Found for " + row.item_code + " in Price List " + frm.doc.custom_price_list);
                            frappe.model.set_value(cdt, cdn, "sale_price", 0);
                        }
                    }
                });
            } else {
                frappe.msgprint("‚ö†Ô∏è No Price List selected while choosing Item Code");
            }
        } else {
            frappe.msgprint("‚ö†Ô∏è Item Code is empty");
        }
    },

    discount_type: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        calculate_discount(row, cdt, cdn);
    },

    discount_percentage: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        calculate_discount(row, cdt, cdn);
    },

    discount_amount: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        calculate_discount(row, cdt, cdn);
    }
});


// üîΩ Helper Function
function calculate_discount(row, cdt, cdn) {
    let sale_price = flt(row.sale_price) || 0;
    let price_after_discount = sale_price;

    if (row.discount_type === "Discount Percentage") {
        let discount_percentage = flt(row.discount_percentage) || 0;
        price_after_discount = sale_price - (sale_price * discount_percentage / 100);

    } else if (row.discount_type === "Discount Amount" || row.discount_type === "Rate") {
        let discount_amount = flt(row.discount__amount) || 0;
        price_after_discount = sale_price - discount_amount;
    }

    frappe.model.set_value(cdt, cdn, "price_after_discount", price_after_discount);
}
