script:
frappe.ui.form.on("promotion", {
    setup(frm) {
        console.log("Promotion form setup triggered", frm);

        // Restrict UOM in item_table rows to only the item's defined UOMs
        frm.set_query("uom", "item_table", function (doc, cdt, cdn) {
            const row = locals[cdt][cdn];
            console.log("Set query for UOM called for row:", row);

            if (!row?.item_code) {
                console.log("No item_code in row, returning empty query");
                return {};
            }

            console.log("Applying filter for item_code:", row.item_code);
            return {
                query: "erpnext.controllers.queries.get_item_uom_query",
                filters: { item_code: row.item_code },
            };
        });

        console.log("Setup complete for promotion form");
    },
});

frappe.ui.form.on("Item child table", {
    item_code(frm, cdt, cdn) {
        const row = locals[cdt][cdn];
        console.log("item_code change detected in child table row:", row);

        if (!row) {
            console.log("Row not found, aborting");
            return;
        }

        console.log(`Clearing UOM for row with item_code ${row.item_code}`);
        frappe.model.set_value(cdt, cdn, "uom", "");

        console.log("UOM cleared for row:", locals[cdt][cdn]);
    },
});

frappe.ui.form.on('Item child table', {
    item_code: function(frm, cdt, cdn) {
        frappe.msgprint("🆕 Item Code field triggered");

        let row = locals[cdt][cdn];
        frappe.msgprint("📌 Selected Item Code: " + row.item_code);

        if (row.item_code) {
            if (frm.doc.custom_price_list) {
                frappe.msgprint("✅ Price List exists: " + frm.doc.custom_price_list);

                frappe.call({
                    method: "frappe.client.get_list",
                    args: {
                        doctype: "Item Price",
                        filters: {
                            item_code: row.item_code,
                            price_list: frm.doc.custom_price_list
                        },
                        fields: ["price_list_rate"],
                        limit_page_length: 1
                    },
                    callback: function(res) {
                        frappe.msgprint("📡 API Call Returned for: " + row.item_code);

                        if (res.message && res.message.length > 0) {
                            let price = res.message[0].price_list_rate;
                            frappe.msgprint("✅ Found Price: " + price);
                            frappe.model.set_value(cdt, cdn, "sale_price", price);

                            // also trigger discount calculation when sale price is fetched
                            calculate_discount(row, cdt, cdn);
                        } else {
                            frappe.msgprint("❌ No Price Found for " + row.item_code + " in Price List " + frm.doc.custom_price_list);
                            frappe.model.set_value(cdt, cdn, "sale_price", 0);
                        }
                    }
                });
            } else {
                frappe.msgprint("⚠️ No Price List selected while choosing Item Code");
            }
        } else {
            frappe.msgprint("⚠️ Item Code is empty");
        }
    },

    discount_type: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        calculate_discount(row, cdt, cdn);
    },

    discount_percentage: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        calculate_discount(row, cdt, cdn);
    },

    discount_amount: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        calculate_discount(row, cdt, cdn);
    }
});


// 🔽 Helper Function
function calculate_discount(row, cdt, cdn) {
    let sale_price = flt(row.sale_price) || 0;
    let price_after_discount = sale_price;

    if (row.discount_type === "Discount Percentage") {
        let discount_percentage = flt(row.discount_percentage) || 0;
        price_after_discount = sale_price - (sale_price * discount_percentage / 100);

    } else if (row.discount_type === "Discount Amount" || row.discount_type === "Rate") {
        let discount_amount = flt(row.discount__amount) || 0;
        price_after_discount = sale_price - discount_amount;
    }

    frappe.model.set_value(cdt, cdn, "price_after_discount", price_after_discount);
}

frappe.ui.form.on('Label Printing', {
    item_code: function(frm) {
        if (!frm.doc.item_code) return;

        // Fetch Item and set UOM filter
        frappe.db.get_doc("Item", frm.doc.item_code).then(item => {
            let allowed_uoms = [];

            if (item.uoms && item.uoms.length > 0) {
                allowed_uoms = item.uoms.map(row => row.uom);
            } else if (item.stock_uom) {
                allowed_uoms = [item.stock_uom];
            }

            // Restrict UOM field to allowed UOMs
            frm.set_query("uom", () => {
                return {
                    filters: [["UOM", "name", "in", allowed_uoms]]
                };
            });

            // Optionally auto-set if only one UOM exists
            if (allowed_uoms.length === 1) {
                frm.set_value("uom", allowed_uoms[0]);
            }

            // Do NOT set barcode here — wait for UOM selection
            frm.set_value("barcode", ""); 
        });
    },

    uom: function(frm) {
        if (!frm.doc.item_code || !frm.doc.uom) return;

        // Fetch Item to get barcodes
        frappe.db.get_doc("Item", frm.doc.item_code).then(item => {
            if (item.barcodes && item.barcodes.length > 0) {
                // Find the barcode that matches the selected UOM
                let matched = item.barcodes.find(b => b.uom === frm.doc.uom);
                if (matched) {
                    frm.set_value("barcode", matched.barcode);
                } else {
                    frm.set_value("barcode", ""); // clear if no match
                }
            } else {
                frm.set_value("barcode", ""); // clear if no barcodes
            }
        });
    }
});

frappe.ui.form.on('Label Printing', {
    update_price_from_price_list: function(frm) {
        if (!frm.doc.item_code || !frm.doc.uom || !frm.doc.price_list) {
            frappe.msgprint(__('Please select Item, UOM and Price List first.'));
            return;
        }

        // Fetch Item Price
        frappe.db.get_value("Item Price", 
            {
                item_code: frm.doc.item_code,
                price_list: frm.doc.price_list,
                uom: frm.doc.uom
            },
            "price_list_rate"
        ).then(r => {
            if (r && r.message && r.message.price_list_rate) {
                frm.set_value("price_to_print", r.message.price_list_rate);
            } else {
                frappe.msgprint(__('No Price found for this Item, Price List and UOM.'));
            }
        });
    }
});
