script:
frappe.ui.form.on('Item child table', {
    item_code: function(frm, cdt, cdn) {
        

        let row = locals[cdt][cdn];
        

        if (row.item_code) {
           

            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Item",
                    name: row.item_code
                },
                callback: function(response) {
                    

                    if (response.message) {
                        let item = response.message;
                        

                        let uoms = item.uoms || [];
                        

                        let uom_names = uoms.map(u => u.uom);
                        
                        
                        frappe.model.set_value(cdt, cdn, "uom", "");

                        frm.fields_dict.item_table.grid.update_docfield_property('uom', 'options', uom_names.join("\n"));

                    
                        if (uom_names.length > 0) {
                           
                            frappe.model.set_value(cdt, cdn, "uom", uom_names[0]);
                        } else {
                            console.warn("‚ö†Ô∏è No UOMs found to set default.");
                        }
                    } else {
                        console.warn("‚ùå Item not found for item_code:", row.item_code);
                    }
                }
            });
        } else {
            console.log("üö´ No item_code selected.");
        }
    }
});
